import React from 'react'

getCommentsOfUser = async (username) => {
    const response = await fetch(`https://jsonplaceholder.typicode.com/comments/${username}/`)
    if (response.ok){
      const comment = await response.json()
      this.setState({ comments: [comment, ...this.state.comments ]})

    }
  }

function GetComments(props){

    return(
        <div className="ProfileCard" onLoad={() => props.handleComments(props.comments)}>
            <div className="card-body">
                <div className="left-align">
                    <p className="Profile-name"> {props.comments} <br/>
                        <span className="profile-login">{props.comments.email}</span>
                    </p>
                    <p className="profile-bio">{props.comments.body}</p>
                </div>

            </div>
        </div>
    )
}

export default GetComments
