import React from 'react'
import './ProfileCard.css'
import profile_img from './profile_img.png'
import { Link } from 'react-router-dom'


class ProfileCard extends React.Component{

    state = {
        userID : ''
    }

    handleInputChange = (event) => {
        this.setState({ userID : this.props.profile.id })
    }

    handleCardClick = (event) => {
        //event.preventDefault()
        this.props.getDetails(this.props.profile.id)
        this.setState({ userID : '' })
    }

    render() {

        return(
            <div className="ProfileCard" onClick={this.handleCardClick}> 
                <div className="profile-avatar">
                    <img src={profile_img} alt="profile-img" />
                </div>
                <div className="card-body">
                    <div className="left-align">
                        <p className="Profile-name">  <br/>
                            <span className="profile-login"> </span>
                        </p>
                        <p className="profile-bio"> </p>
                    </div>
                    <div className="right-align">
                        {/* <Link to={ `/comments/${props.profile.id}` } className="load-comment load-btn">Load Comments</Link>
                        <Link to="/posts" className="load-posts load-btn">Load Posts</Link> */}
                        {/* <Link to={`/comments/${props.profile.id}`}  className="load-comment load-btn" >Comments</Link> */}
                    </div>

                </div>
            </div>
        )

    }

}

export default ProfileCard
