import React from 'react'
import './ProfileCard.css'
import profile_img from './profile_img.png'
//import { Link } from 'react-router-dom'


function ProfileCard(props){
    // const path = `/comments/${props.profile.id}`
    return(
        // <Link to={ `/comments/${props.profile.id}` }>

        // </Link>

        <div className="ProfileCard" > 
        <div className="profile-avatar">
            <img src={profile_img} alt="profile-img" />
        </div>
        <div className="card-body">
            <div className="left-align">
                <p className="Profile-name"> {props.profile.name} <br/>
                    <span className="profile-login">{props.profile.email}</span>
                </p>
                <p className="profile-bio">{props.profile.website}</p>
            </div>
            <div className="right-align">
                {/* <Link to={ `/comments/${props.profile.id}` } className="load-comment load-btn">Load Comments</Link>
                <Link to="/posts" className="load-posts load-btn">Load Posts</Link> */}
                <button  className="load-comment load-btn" onClick={() => props.handleClick.bind(this, props.profile.id)}>Comments</button>
            </div>

        </div>
        </div>

    )

}

export default ProfileCard
