import React from 'react';
import AppHeader from './AppHeader';
import ProfileCard from './ProfileCard';
import { CSSTransitionGroup } from 'react-transition-group';
import { BrowserRouter, Route, Link, Switch } from 'react-router-dom'
import './App.css';

class App extends React.Component {

    state = {
        profiles : []
      }

  getInitialProfiles() {
    fetch(`https://jsonplaceholder.typicode.com/users`)
    .then(res => {
          if(res.ok){
            return res.json()
          }else{
            return "Something went wrong"
          }
    })
    .then(profiles => this.setState({ profiles: profiles }))
    .catch(console.error)
  }

  componentDidMount () {
    this.getInitialProfiles()
  }



  render(){
    const profileJsx = this.state.profiles.map(profile =>
        (<ProfileCard profile={profile} />) )


  const LoadComments = () => <div><h2>Page Comment</h2></div>
  const LoadPosts = () => <div><h2>Page Post</h2></div>
  const LoadAllUsers = () => <main className='profileList' style={{ padding: '2rem'}}>
                                <CSSTransitionGroup component="div"
                                  transitionName="fade"
                                  transitionAppear={true}
                                  transitionAppearTimeout={200}
                                  transitionEnterTimeout={250}
                                  transitionLeaveTimeout={225}>
                                  {profileJsx}
                                </CSSTransitionGroup>
                              </main>

    return (
      <BrowserRouter>
        <div className='App'>
          <AppHeader />
          <Switch>
              <Route exact path="/" component={LoadAllUsers} />
              <Route exact path="/comments" component={LoadComments} />
              <Route exact path='/posts' component={LoadPosts} />
          </Switch>
        </div>
      </BrowserRouter>
    )
  };

}

export default App
