import './App.css';

class App extends React.Component {

    state = {
        maddProps : ['1','2','3','4', '5', '6','7','8'],
        profiles : []
      }

  getInitialProfiles() {
    Promise.all(this.state.maddProps.map(
        username => fetch(`https://jsonplaceholder.typicode.com/users/${username}`)
      )
    ).then(responses => Promise.all(responses.map(res => {
          if(res.ok){
            return res.json()
          }else{
            return "Something went wrong"
          }
        })
      )
    )
    .then(profiles => this.setState({ profiles: profiles }))
    .catch(console.error)
  }

