import React from 'react';
import AppHeader from './AppHeader';
import ProfileCard from './ProfileCard';
import GetComments from './GetComments';
import { CSSTransitionGroup } from 'react-transition-group';
import { BrowserRouter, Route, Link, Switch } from 'react-router-dom'
import './App.css';

class App extends React.Component {

    state = {
        profiles : [],
        comments : [],
        userId : null
      }

  getInitialProfiles() {
    fetch(`https://jsonplaceholder.typicode.com/users`)
    .then(res => {
          if(res.ok){
            return res.json()
          }else{
            return "Something went wrong"
          }
    })
    .then(profiles => this.setState({ profiles: profiles }))
    .catch(console.error)
  }

  componentDidMount () {
    this.getInitialProfiles()
  }



  getCommentsOfUser = async (username) => {
    const response = await fetch(`https://jsonplaceholder.typicode.com/comments/${username}`)
    if (response.ok){
      const comment = await response.json()
      this.setState({ comments: [comment, ...this.state.comments ]})
      this.setState({userId})
    }
  }

  render(){
    const profileJsx = this.state.profiles.map(profile =>
        (<Link to=""><ProfileCard profile={profile} /></Link> ) )

    const com = this.state.comments.map(comment =>(
      <GetComments comment={comment} />
    ))




  const LoadPosts = () => <div><h2> Page Post </h2></div>
  const LoadComment = () => <main className="">
    { com }
  </main>
  const LoadAllUsers = () => <main className='profileList' style={{ padding: '2rem'}}>
                                <CSSTransitionGroup component="div"
                                  transitionName="fade"
                                  transitionAppear={true}
                                  transitionAppearTimeout={200}
                                  transitionEnterTimeout={250}
                                  transitionLeaveTimeout={225}>
                                  {profileJsx}
                                </CSSTransitionGroup>
                              </main>

    const pathToComments = `/comments/${this.userId}`

    return (

      <BrowserRouter>
        <div className='App'>
          <AppHeader />
          <Switch>
              <Route exact path="/" component={LoadAllUsers} />
              <Route exact path={pathToComments} component={LoadComment} />
              <Route exact path='/posts' component={LoadPosts} />
          </Switch>
        </div>
      </BrowserRouter>
    )
  };

}

export default App
