import React from 'react'



class GetComments extends React.Component {

    tate = {
        comments : []
      }

      getCommentsOfUser = async (username) => {
        const response = await fetch(`https://jsonplaceholder.typicode.com/comments/${username}/`)
        if (response.ok){
          const comment = await response.json()
          this.setState({ comments: [comment, ...this.state.comments ]})

        }
      }

    render(){
        return(
            <div className="ProfileCard">
                <div className="card-body">
                    <div className="left-align">
                        <p className="Profile-name"> {this.comments} <br/>
                            <span className="profile-login">{this.comments.email}</span>
                        </p>
                        <p className="profile-bio">{this.comments.body}</p>
                    </div>

                </div>
            </div>
        )
    }
}

export default GetComments
