import React from 'react';
import AppHeader from './AppHeader';
import ProfileCard from './ProfileCard';
import './App.css';
import AddProfileForm from './AddProfileForm';
//import profiles from './Profiles.json';

class App extends React.Component {

  // constructor (props) {
  //   super(props)
  //   this.state = {
  //     maddProps : ['gond0017', 'harshtheone', 'adeshah'],
  //     profiles : [],
  //     username : ''
  //   }
  // }

    state = {
        maddProps : ['prof3ssorSt3v3','papacharliepapa','rlmckenney','gond0017', 'harshtheone', 'adeshah'],
        profiles : []
      }

  getInitialProfiles() {
    Promise.all(this.state.maddProps.map(
        username => fetch(`https://api.github.com/users/${username}`)
      )
    ).then(responses => Promise.all(responses.map(res => {
          if(res.ok){
            return res.json()
          }else{
            return "Something went wrong"
          }
        })
      )
    )
    .then(profiles => this.setState({ profiles: profiles }))
    .catch(console.error)
  }

  getProfile = async (username) => {
    const response = await fetch(`https://api.github.com/users/${username}`)
    if (response.ok){
      const profile = await response.json()

      this.setState({ profiles: [profile, ...this.state.profiles ]})

    }
  }

  removeProfile = targetProfile => {
    const profiles = this.state.profiles.filter(
      profile => profile.id !== targetProfile.id
    )
    this.setState({ profiles })
  }

  // handleFormSubmit = (event) => {
  //   event.preventDefault()
  //   this.getProfile(this.state.username)

  // }

  componentDidMount () {
    this.getInitialProfiles()
  }

  render(){
    const profileJsx = this.state.profiles.map(profile =>
        (<ProfileCard key={profile.id} profile={profile} handleRemove={this.removeProfile} />) )

    return (
      <div className='App'>
         <AppHeader />

         <AddProfileForm handleSubmit={this.getProfile} />

         <main className='profileList' style={{ padding: '2rem'}}>
            {profileJsx}
         </main>

      </div>
    )
  };

}

export default App
