import React from 'react'

class GetComments extends React.Component{

    state = {
        comments : []
    }
    dataId = this.props.dataId
    getCommentsOfUser = async (dataId) => {
        alert("hi")
        const response = await fetch(`https://jsonplaceholder.typicode.com/comments/${dataId}/`)
        if (response.ok){
            const comment = await response.json()
            this.setState({ comments: [comment, ...this.state.comments ]})
            //return <Redirect to={ `/comments/${username}` } />
        }
    }

    render() {

        return(
            <div className="ProfileCard" >
                <div className="card-body">
                    <div className="left-align">
                        <p className="Profile-name"> { this.state.comments.name } <br/>
                            <span className="profile-login">{ this.state.comments.email }</span>
                        </p>
                        <p className="profile-bio">{ this.state.comments.body }</p>
                    </div>

                </div>
            </div>
        )
    }
}

export default GetComments
