import React from 'react'

getCommentsOfUser = async (userId) => {
    const response = await fetch(`https://jsonplaceholder.typicode.com/comments/${userId}`)
    if (response.ok){
      const comment = await response.json()
      console.log(comment)
      this.setState({ comments: [comment, ...this.state.comments ]})

    }
  }
function GetComments(props){

    return(
        <div className="ProfileCard"> 

            <div className="card-body">
                <div className="left-align">
                    <p className="Profile-name"> {props.comment.name} <br/>
                        <span className="profile-login">{props.comment.email}</span>
                    </p>
                    <p className="profile-bio">{props.comment.body}</p>
                </div>

            </div>
        </div>
    )

}

export default GetComments
